import { t } from 'i18next';
import { getCurrentProjectEditor } from '../app/main.js';
import { addAsChildren, makeElement } from '../common/dom.js';
import { makeActionsArea_Path } from './actions.js';
import {
	makeInputs_position,
	makeInputs_size,
	makeSingleInput,
	makeSingleLabel,
	rowPad,
} from './cards.js';

// --------------------------------------------------------------
// Path Attributes Card
// --------------------------------------------------------------

export function makeCard_pathAttributes(path) {
	// log(`makeCard_pathAttributes`, 'start');
	// log(path);

	// `ident` is a unique ID per object for debugging
	let pathCard = makeElement({
		tag: 'div',
		className: 'panel__card',
		innerHTML: `<h3>${t('ui:objType.Path')} ${path.ident || ''}</h3>`,
	});

	// Path name
	// log(`path.name: ${path.name}`);
	let nameLabel = makeSingleLabel(t('ui:path.pathName'));
	let nameInput = makeSingleInput(path, 'name', 'currentPath', 'input');

	// Winding
	let windingInfo = makeElement({
		tag: 'label',
		className: 'info',
		innerHTML: `
			<span>${t('ui:path.winding')}</span>
			<info-bubble>
				<h1>${t('ui:path.winding2')}</h1>
				${t('ui:path.windingTips1')}
				<br><br>
				${t('ui:path.windingTips2')}
				<br><br>
				<div style="display: grid; gap: 10px; grid-template-columns: 1fr 1fr; width: 350px;">
					<span>${t('ui:path.windingTips3')}</span>
					<span>${t('ui:path.windingTips4')}</span>
					<img
						alt="${t('ui:path.windingTips5')}"
						class="winding-help-image"
						src="data:image/png;base64,"
					>
					<img
						alt="${t('ui:path.windingTips6')}"
						class="winding-help-image"
						src="data:image/png;base64,"
					>
				</div>
			</info-bubble>
		`,
	});

	let windingToggle = makeElement({
		tag: 'option-toggle',
		innerHTML: `<option>${t('ui:path.clockwise')}</option><option>${t(
			'ui:path.counterClockwise'
		)}</option>`,
		attributes: {
			'selected-name': getWindingName(path.winding),
			'selected-value': getWindingName(path.winding),
		},
		onClick: () => {
			const editor = getCurrentProjectEditor();
			path.reverseWinding();
			editor.history.addState(`Toggled path winding to ${getWindingName(path.winding)}`);
			editor.publish('currentPath', path);
		},
	});

	// Position and Size
	let positionInputs = makeInputs_position(path);
	let sizeInputs = makeInputs_size(path);

	// Put it all together
	addAsChildren(pathCard, [
		nameLabel,
		nameInput,
		windingInfo,
		windingToggle,
		positionInputs,
		sizeInputs,
	]);
	addAsChildren(pathCard, rowPad());
	addAsChildren(pathCard, makeActionsArea_Path());

	// log(`makeCard_pathAttributes`, 'end');
	return pathCard;
}

function getWindingName(winding) {
	let name = 'Unknown';
	if (winding > 0) name = t('ui:path.clockwise');
	if (winding < 0) name = t('ui:path.counterClockwise');
	return name;
}

export function makeCard_multiSelectPathAttributes(virtualGlyph) {
	let multiPathCard = makeElement({
		tag: 'div',
		className: 'panel__card',
		innerHTML: `<h3>${virtualGlyph.shapes.length} selected shapes</h3>`,
	});

	addAsChildren(multiPathCard, makeInputs_position(virtualGlyph));
	addAsChildren(multiPathCard, makeInputs_size(virtualGlyph));
	addAsChildren(multiPathCard, makeActionsArea_Path());

	return multiPathCard;
}
